    /**
     * Is empty or has white space boolean.
     *
     * @param string the string
     * @return the boolean
     */
    public Boolean isEmptyOrHasWhiteSpace(String string){
        return ( string.trim().isEmpty() || string.trim().contains(" ") );
    }

    /**
     * Has special characters boolean.
     *
     * @param string the string
     * @return the boolean
     */
    public Boolean hasSpecialCharacters(String string){
        //Check is the string has anything other than a-z and 0-9 case insensitive
        return Pattern.compile("[^a-z0-9 ]", Pattern.CASE_INSENSITIVE)
                .matcher(string)
                .find();
    }

    /**
     * Has number boolean.
     *
     * @param string the string
     * @return the boolean
     */
    public Boolean hasNumber(String string){
        return !Pattern.compile("[^0-9 ]").matcher(string).find();
    }


    //-------------------- Validation Functions --------------------

    /**
     * Is email valid boolean.
     *
     * @param email the email
     * @return the boolean
     */
    public Boolean isEmailValid(String email){
        return ( isEmail(email) && !isEmptyOrHasWhiteSpace(email) );
    }

    /**
     * Is first name and last name valid boolean.
     *
     * @param firstName the first name
     * @param lastName  the last name
     * @return the boolean
     */
    public Boolean isFirstNameAndLastNameValid(String firstName, String lastName){
        return !isEmptyOrHasWhiteSpace(firstName)
                && !isEmptyOrHasWhiteSpace(lastName)
                && !hasSpecialCharacters(firstName)
                && !hasSpecialCharacters(lastName)
                && !hasNumber(firstName)
                && !hasNumber(lastName);
    }
